import React, { useEffect, useState } from 'react';
import * as fabric from 'fabric'; // v6

var LabeledRect = fabric.createClass(fabric.Rect, {

  type: 'labeledRect',
  // initialize can be of type function(options) or function(property, options), like for text.
  // no other signatures allowed.
  initialize: function(options) {
    options || (options = { });

    this.callSuper('initialize', options);
    this.set('label', options.label || '');
  },

  toObject: function() {
    return fabric.object.extend(this.callSuper('toObject'), {
      label: this.get('label')
    });
  },

  _render: function(ctx) {
    this.callSuper('_render', ctx);

    ctx.font = '5px Helvetica';
    ctx.fillStyle = '#333';
    ctx.fillText(this.label, -this.width/2, -this.height/2 + 20);
  }
});

export const App = () => {
  const [canvas, setCanvas] = useState('');
  const [selectedObject, setSelectedObject] = useState('');

  useEffect(() => {
    const options = { 
      width: 500,
      height: 500,
      backgroundColor: 'gray'
      };
    const canvas = new fabric.Canvas('canvas', options);
    canvas.selectionColor = 'rgba(0,255,0,0.3)';
    canvas.selectionBorderColor = 'red';
    canvas.selectionLineWidth = 5;
    canvas.on("mouse:down", handleMouseDown);

    setCanvas(canvas);
    canvas.renderAll();

    return () => {
      canvas.dispose();
    }
  }, []);

  const addSquare = parentCanvas => {
    const shape = new LabeledRect({
      top: 50,
      left: 50,
      height: 50,
      width: 50,
      strokeWidth: 1,
      stroke: 'blue',
      fill: 'white',
      label: 'T'
    });
    parentCanvas.add(shape);
    parentCanvas.renderAll();
  }

  const addDiamond = parentCanvas => {
    const shape = new LabeledRect({
      top: 50,
      left: 50,
      height: 50,
      width: 50,
      strokeWidth: 1,
      stroke: 'blue',
      fill: 'white',
      angle: 45,
      label: 'T'
    });
    parentCanvas.add(shape);
    parentCanvas.renderAll();
  }



  const clearCanvas = parentCanvas => {
    parentCanvas.remove(...canvas.getObjects());
    setSelectedObject('');
  }

  const handleMouseDown = event => {
    if (event.target) {
      setSelectedObject(event.target);
      console.log(event.target);
    } else {
      setSelectedObject('');
    }
  }

  const deleteSelection = (parentCanvas, obj) => {
    if (obj) {
      parentCanvas.remove(obj);
      setSelectedObject('');
    }
  }

  // return <canvas width="300" height="300" ref={canvasEl}/>;
  // return <canvas width="300" height="300" id='canvas'/>;
  return(
    <div>
      <h1>Fabric.js on React - fabric.Canvas('...')</h1>
      <button onClick={() => addSquare(canvas)}>Square</button>
      <button onClick={() => addDiamond(canvas)}>Diamond</button>
      <button onClick={() => clearCanvas(canvas)}>Clear Canvas</button>
      <button onClick={() => deleteSelection(canvas, selectedObject)}>Delete Selection</button>
     <br/><br/>

      <canvas id="canvas" />
    </div>
  );
};

export default App;
