import React, { useEffect, useState } from 'react';
import * as fabric from 'fabric'; // v6


export const App = () => {
  const [canvas, setCanvas] = useState('');
  const [selectedObject, setSelectedObject] = useState('');

  useEffect(() => {
    const options = { 
      width: 500,
      height: 500,
      backgroundColor: 'gray'
      };
    const canvas = new fabric.Canvas('canvas', options);

    canvas.on("mouse:down", handleMouseDown);

    setCanvas(canvas);
    canvas.renderAll();

    return () => {
      canvas.dispose();
    }
  }, []);

  const addSquare = parentCanvas => {
    const shape = new fabric.Rect({
      top: 50,
      left: 50,
      height: 50,
      width: 50,
      strokeWidth: 1,
      stroke: 'blue',
      fill: 'white'
    });
    parentCanvas.add(shape);
    parentCanvas.renderAll();
  }

  const addDiamond = parentCanvas => {
    const shape = new fabric.Rect({
      top: 50,
      left: 50,
      height: 50,
      width: 50,
      strokeWidth: 1,
      stroke: 'blue',
      fill: 'white',
      angle: 45
    });
    parentCanvas.add(shape);
    parentCanvas.renderAll();
  }

  const clearCanvas = parentCanvas => {
    parentCanvas.remove(...canvas.getObjects());
  }

  const handleMouseDown = event => {
    if (event.target) {
      setSelectedObject(event.target);
      console.log(event.target);
    }
  }

  // return <canvas width="300" height="300" ref={canvasEl}/>;
  // return <canvas width="300" height="300" id='canvas'/>;
  return(
    <div>
      <h1>Fabric.js on React - fabric.Canvas('...')</h1>
      <button onClick={() => addSquare(canvas)}>Square</button>
      <button onClick={() => addDiamond(canvas)}>Diamond</button>
      <button onClick={() => clearCanvas(canvas)}>Clear Canvas</button>
      <button onClick={() => deleteSelection(canvas)}>Delete Selection</button>
     <br/><br/>

      <canvas id="canvas" />
    </div>
  );
};

export default App;
