export const scalingYOrSkewingX: TransformActionHandler = (
  eventData,
  transform,
  x,
  y
) => {
  return isAltAction(eventData, transform.target)
    ? skewHandlerX(eventData, transform, x, y)
    : scalingY(eventData, transform, x, y);
};
