export class StrokeLineJoinProjections extends StrokeProjectionsBase {
  /**
   * The point being projected (the angle BAC)
   */
  declare A: Point;
  /**
   * The point before A
   */
  declare B: Point;
  /**
   * The point after A
   */
  declare C: Point;
  /**
   * The AB vector
