export const App = () => {
  useEffect(() => {
    canvas = new fabric.Canvas('canvas', {
      isDrawingMode: true,
      selection: true,
      hoverCursor: 'pointer',
   });
  });

  canvas = new fabric.Canvas('canvas', {
    isDrawingMode: true,
    selection: true,
    hoverCursor: 'pointer',
    height: 375,
    width: 375,
  });
  canvas.on('mouse:up', () => { canvas.item(canvas._objects.length - 1).selectable = false; });
  canvas.freeDrawingBrush.width = 25;
  canvas.freeDrawingBrush.color = 'blue';

  return <canvas width="300" height="300" ref={canvas}/>;
};

export default App;
